mapper.select:
SELECT O.idx, O.order_code AS orderCode, DATE_FORMAT(O.insert_date, '%Y.%m.%d %H:%m:%s') insertDate, O.total_price AS totalPrice, O.print_price AS printPrice, O.State AS state, B.state bState, IFNULL(C.price, 0) AS cpPrice, C.title AS cpTitle, B.title AS bTitle, B.box_cnt AS boxCnt, B.ad_price AS adPrice, B.box_code AS boxCode, B.unit, DATE_FORMAT(B.sell_date, '%Y.%m') AS sellDate, BM.name AS bName, A.title AS aTitle, A.ad_code AS adCode, A.url, A.content, A.idx aIdx, QR.url qrcode, QR.idx AS qIdx, DATE_FORMAT(A.ad_start_date, '%y.%m.%d') AS adStartDate, DATE_FORMAT(A.ad_end_date, '%y.%m.%d') AS adEndDate, AM.name AS aName, GROUP_CONCAT(CA.name) AS cNames, CONCAT(P1.name, ' ', P2.name) AS proType, CONCAT(P3.name, ' ', P4.name) AS aProType, CONCAT(C2.name, ' > ', C1.name) AS cType, (CASE WHEN A.state = 'N' THEN '광고대기' WHEN A.state = 'Y' THEN '광고중' WHEN A.state = 'P' THEN '일시중지' WHEN A.state = 'E' THEN '광고종료' END) AS aState, (CASE WHEN O.state = 'N' THEN '광고대기' WHEN O.state = 'B1' THEN '광고구매' WHEN O.state = 'B2' THEN '광고승인' WHEN O.state = 'B3' THEN '광고진행' WHEN O.state = 'B4' THEN '1차광고인증' WHEN O.state = 'B5' THEN '2차광고인증' WHEN O.state = 'B6' THEN '광고완료' WHEN O.state = 'B7' THEN '구매취소' WHEN O.state = 'B8' THEN '판매종료' WHEN O.state = 'A1' THEN '광고신청' WHEN O.state = 'A2' THEN '신청승인' WHEN O.state = 'A3' THEN '광고집행' WHEN O.state = 'A4' THEN '신청취소' END) AS oState, (CASE WHEN R.state = 'B0' THEN '발주취소' WHEN R.state = 'B4' THEN '신규발주' WHEN R.state = 'B5' THEN '디자인 검수' WHEN R.state = 'B6' THEN '디자인 수정' WHEN R.state = 'B7' THEN '홍보물 제작중' WHEN R.state = 'B8' THEN '홍보물 제작완료' WHEN R.state = 'B9' THEN '배송처리' END) AS rState FROM order_info O LEFT JOIN coupon_manage C ON C.idx = O.c_idx LEFT JOIN member M ON M.idx = O.m_idx LEFT JOIN box_product B ON B.idx = O.b_idx LEFT JOIN member BM ON B.writer = BM.idx LEFT JOIN ad_product A ON A.idx = O.a_idx LEFT JOIN member AM ON A.writer = AM.idx LEFT JOIN code_manage P2 ON B.p_c_idx = P2.idx LEFT JOIN code_manage P1 ON P2.parent = P1.idx LEFT JOIN code_manage P4 ON A.p_c_idx = P4.idx LEFT JOIN code_manage P3 ON P4.parent = P3.idx LEFT JOIN code_manage C1 ON B.c_c_idx = C1.idx LEFT JOIN code_manage C2 ON C1.parent = C2.idx LEFT JOIN code_manage CA ON FIND_IN_SET(CA.idx, A.c_c_idx) LEFT JOIN print_log R ON R.idx = (SELECT idx FROM print_log WHERE O.idx = o_idx ORDER BY insert_date DESC LIMIT 1) LEFT JOIN qrcode QR ON QR.parent = A.idx
GROUP BY O.idx ORDER BY O.insert_date desc
LIMIT #{start}, #{end}

mapper.update:
UPDATE order_info
   SET state = #{state}, update_date = NOW()
   WHERE idx = #{idx}

UPDATE order_info 
SET state = #{state}
WHERE idx = #{idx}
