mapper.select:
SELECT B.idx ,B.title ,B.content ,B.type ,B.insert_date ,B.state ,B.notice_flag ,B.category ,B.ad_group ,B.writer ,B.view_cnt ,F.parent ,A.name ,C.name categoryName ,IF(B.insert_date > date_add(now(), INTERVAL - 14 DAY), 'T', 'F') newFlag FROM board B LEFT JOIN admin A ON B.writer = A.idx LEFT JOIN file F ON B.idx = F.parent AND F.type = 'N' LEFT JOIN code_manage C ON B.category = C.idx
AND B.state = 'Y' GROUP BY B.idx ORDER BY B.notice_flag DESC, B.insert_date DESC
LIMIT #{start}, #{end}

mapper.delete:
DELETE FROM board
		WHERE idx = #{idx} 

			AND B.type = #{type}

mapper.select:
SELECT DISTINCT B.idx ,B.title ,B.content ,B.insert_date ,B.view_cnt ,(CASE WHEN B.reply_flag = 'Y' THEN '답변완료' ELSE '답변대기' END) reply_flag ,A.name ,A.id email ,F.parent ,(SELECT insert_date FROM board_qna WHERE parent = B.idx ORDER BY insert_date desc LIMIT 1) replyDate FROM board_qna B LEFT JOIN member A ON B.writer = A.idx LEFT JOIN file F ON F.parent = B.idx
ORDER BY B.insert_date DESC
LIMIT #{start}, #{end}
